    public void saveAndGet() {
        Document document = documentGenerator.getDocument();

        // add it to a Set before saving (force equals/hashcode)
        Set<Document> set = newHashSet(document, document);
        assertThat(set).hasSize(1);

        documentDao.save(document);
        entityManager.flush();

        // reload it from cache and check equality
        Document model = new Document();
        model.setId(document.getId());
        assertThat(document).isEqualTo(documentDao.get(model));

        // clear cache to force reload from db
        entityManager.clear();

        // pk are equals...
        assertThat(document.getId()).isEqualTo(documentDao.get(model).getId());

        // but since you do not use a business key, equality is lost.
        assertThat(document).isNotEqualTo(documentDao.get(model));
    }
